import React from 'react'
import  "./app.css";



const TodoList = (props) => {

  const listofItems = () => {
    setItems((oldItems) => {
      return [...oldItems, inputList];
    });
    setInputList("");
};
  return ( 
  <>
    <div className="todolist">
    <button onClick={listofItems}> + </button>
       <i className ="fa fa-trash-o" 
      aria-hidden="true"
      onClick={() => {
      props.onSelect(props.id)
      } }
      />
                <li>{props.text}</li>
          </div>
  </>
  )
};

export default TodoList;
